import React, { useEffect, useState } from "react";
import API from "../api";

export default function Profile() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem("token");
    API.get("/api/orders/my-orders", {
      headers: { Authorization: token ? `Bearer ${token}` : undefined },
    })
      .then((res) => setOrders(res.data))
      .catch((err) => console.error("❌ Orders fetch error:", err))
      .finally(() => setLoading(false));
  }, []);

  if (loading) {
    return <p className="loading-text">Loading your orders...</p>;
  }

  return (
    <div className="profile-page">
      <h2 className="page-title">My Orders</h2>

      {orders.length === 0 ? (
        <p className="empty-text">You haven’t placed any orders yet.</p>
      ) : (
        <div className="orders-list">
          {orders.map((order) => (
            <div key={order._id} className="order-card">
              <div className="order-header">
                <h4 className="order-id">Order #{order._id.slice(-6)}</h4>
                <span className="order-status">{order.status}</span>
              </div>

              <p className="order-total">
                <strong>Total:</strong> £{order.totalAmount.toFixed(2)}
              </p>
              <p className="order-date">
                <strong>Date:</strong>{" "}
                {new Date(order.createdAt).toLocaleString()}
              </p>

              <div className="order-items">
                <strong>Items:</strong>
                <ul>
                  {order.items.map((item, i) => (
                    <li key={i}>
                      {item.name} × {item.qty} — £
                      {(item.price * item.qty).toFixed(2)}
                    </li>
                  ))}
                </ul>
              </div>

              <div className="order-address">
                <strong>Delivery Address:</strong>
                <p>{order.address?.street}</p>
                <p>
                  {order.address?.postcode}, {order.address?.county}
                </p>
                <p>{order.address?.country}</p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
