import React, { useMemo } from "react";
import { useLocation, Link } from "react-router-dom";

export default function OrderConfirmation() {
  const location = useLocation();
  const { orderId, total } = location.state || {};

  // Generate a random delivery time between 20â€“45 minutes
  const eta = useMemo(() => {
    const minutes = Math.floor(Math.random() * 26) + 20;
    const deliveryTime = new Date();
    deliveryTime.setMinutes(deliveryTime.getMinutes() + minutes);
    return { minutes, deliveryTime };
  }, []);

  return (
    <div className="order-confirmation">
      <h2>ðŸŽ‰ Thank you for your order!</h2>

      {orderId ? (
        <div className="order-details">
          <p>Your order has been placed successfully.</p>
          <p>
            <strong>Order ID:</strong> #{orderId.slice(-6)}
          </p>
          <p>
            <strong>Total Paid:</strong> Â£{total.toFixed(2)}
          </p>
          <p>
            <strong>Estimated Delivery:</strong> {eta.minutes} minutes
            (around {eta.deliveryTime.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" })})
          </p>
        </div>
      ) : (
        <p>We couldnâ€™t fetch order details, but your payment was successful.</p>
      )}

      <div className="order-actions">
        <Link to="/profile" className="btn-primary">
          View My Orders
        </Link>
        <Link to="/" className="btn-secondary">
          Continue Shopping
        </Link>
      </div>
    </div>
  );
}
