// client/src/components/Recommendations.js
import React, { useState, useEffect } from "react";
import API from "../api";

export default function Recommendations({ restaurantId }) {
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRecommendations = async () => {
      try {
        const { data } = await API.get(`/api/recommendations/${restaurantId}`);
        setRecommendations(data);
      } catch (err) {
        console.error("Failed to fetch recommendations:", err);
        setError("Could not load recommendations");
      } finally {
        setLoading(false);
      }
    };

    if (restaurantId) {
      fetchRecommendations();
    }
  }, [restaurantId]);

  if (loading) return <div>Loading recommendations...</div>;
  if (error) return null; // Hide component on error
  if (!recommendations?.length) return null;

  return (
    <div className="recommendations">
      <h3>Recommended for You</h3>
      <div className="recommendations-grid">
        {recommendations.map((item) => (
          <div key={item._id} className="recommendation-card">
            {item.image && (
              <img
                src={item.image}
                alt={item.name}
                className="recommendation-image"
              />
            )}
            <div className="recommendation-details">
              <h4>{item.name}</h4>
              <p>{item.description}</p>
              <p className="price">Â£{item.price.toFixed(2)}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
