import React, { useContext } from "react";
import { CartContext } from "../context/CartContext";

export default function Cart() {
  const { cart, setCart } = useContext(CartContext);

  const updateQty = (id, delta) => {
    setCart(
      (prev) =>
        prev
          .map((item) =>
            item.menuItemId === id ? { ...item, qty: item.qty + delta } : item
          )
          .filter((item) => item.qty > 0) // remove if qty goes 0
    );
  };

  const removeItem = (id) => {
    setCart((prev) => prev.filter((item) => item.menuItemId !== id));
  };

  const total = cart.reduce((sum, item) => sum + item.price * item.qty, 0);

  if (cart.length === 0) {
    return <div style={{ padding: "20px" }}>ðŸ›’ Your cart is empty</div>;
  }

  return (
    <div className="cart-page">
      <h1>Your Cart</h1>
      <div className="cart-items">
        {cart.map((item) => (
          <div key={item.menuItemId} className="cart-item">
            <div>
              <h3>{item.name}</h3>
              <p>${item.price.toFixed(2)}</p>
            </div>
            <div className="cart-controls">
              <button onClick={() => updateQty(item.menuItemId, -1)}>-</button>
              <span>{item.qty}</span>
              <button onClick={() => updateQty(item.menuItemId, 1)}>+</button>
              <button
                onClick={() => removeItem(item.menuItemId)}
                className="remove-btn"
              >
                ðŸ—‘ Remove
              </button>
            </div>
          </div>
        ))}
      </div>

      <div className="cart-total">
        <h2>Total: ${total.toFixed(2)}</h2>
        <button className="checkout-btn">Proceed to Checkout</button>
      </div>
    </div>
  );
}
