import React, { useState, useEffect } from 'react';
import API from '../api';

export default function Cart(){
  const [cart, setCart] = useState(JSON.parse(localStorage.getItem('cart') || '[]'));
  const [address, setAddress] = useState('');

  useEffect(()=> localStorage.setItem('cart', JSON.stringify(cart)), [cart]);

  function changeQty(index, delta){
    const c = [...cart];
    c[index].qty = Math.max(1, c[index].qty + delta);
    setCart(c);
  }

  async function placeOrder(){
    const user = JSON.parse(localStorage.getItem('user') || 'null');
    if(!user){ alert('Please login'); return; }
    const total = cart.reduce((s,i) => s + i.price * i.qty, 0);
    try {
      const res = await API.post('/orders', { items: cart, total, address });
      alert('Order placed!');
      setCart([]);
    } catch(err){
      console.error(err);
      alert('Error placing order');
    }
  }

  return (
    <div>
      <h1>Your cart</h1>
      {cart.length === 0 && <p>Cart empty</p>}
      {cart.map((it, idx)=> (
        <div key={it.menuItemId} style={{ display:'flex', gap:12, alignItems:'center', padding:8 }}>
          <div style={{flex:1}}>{it.name}</div>
          <div>${(it.price * it.qty).toFixed(2)}</div>
          <div>
            <button onClick={()=> changeQty(idx,-1)}>-</button>
            <span style={{ margin: '0 8px'}}>{it.qty}</span>
            <button onClick={()=> changeQty(idx,1)}>+</button>
          </div>
        </div>
      ))}
      {cart.length>0 && <>
        <div style={{ marginTop:16 }}>
          <label>Delivery address</label><br/>
          <input value={address} onChange={e=>setAddress(e.target.value)} style={{ width:'100%', padding:8 }} />
        </div>
        <button onClick={placeOrder} style={{ marginTop:12 }}>Place Order</button>
      </>}
    </div>
  );
}
