// client/src/pages/Profile.js
import React, { useEffect, useState, useContext } from "react";
import API from "../api";
import { CartContext } from "../context/CartContext";
import { useNavigate } from "react-router-dom";
import DeliveryTracker from "../components/DeliveryTracker";

// Small helper to color status badges consistently
const statusBg = (status) => {
  switch (status) {
    case "Paid":
      return "#17a2b8"; // teal
    case "Preparing":
      return "#ff9800"; // orange
    case "Out for Delivery":
      return "#007bff"; // blue
    case "Delivered":
      return "#28a745"; // green
    default:
      return "#6c757d"; // gray
  }
};

export default function Profile() {
  const [orders, setOrders] = useState([]);
  const [expandedOrders, setExpandedOrders] = useState({});
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const { addToCart } = useContext(CartContext);

  const fetchOrders = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setOrders([]);
        setLoading(false);
        return;
      }
      const res = await API.get("/api/orders/my-orders", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setOrders(Array.isArray(res.data) ? res.data : []);
    } catch (err) {
      console.error("Failed to fetch orders:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchOrders();
    const interval = setInterval(fetchOrders, 5000);
    return () => clearInterval(interval);
  }, []);

  const toggleTracker = (orderId) => {
    setExpandedOrders((prev) => ({ ...prev, [orderId]: !prev[orderId] }));
  };

  // Re-adds items from a past order to the cart
  const handleReorder = (order) => {
    const restaurantId = order?.restaurant?._id || order?.restaurant || null;
    order.items.forEach((item) => {
      // Synthetic _id for cart uniqueness when order items don't have _id
      const syntheticId = `${restaurantId || "unknown"}:${item.name}`;
      addToCart(
        {
          _id: syntheticId,
          name: item.name,
          price: item.price,
          qty: item.qty || 1,
          restaurantId,
        },
        restaurantId
      );
    });
    navigate("/cart");
  };

  const activeOrders = orders.filter((o) => o.status !== "Delivered");
  const deliveredOrders = orders.filter((o) => o.status === "Delivered");

  if (loading) {
    return (
      <div style={{ padding: "30px" }}>
        <h2 style={{ margin: 0 }}>My Orders</h2>
        <p style={{ color: "#666" }}>Loading your orders‚Ä¶</p>
      </div>
    );
  }

