import React, {useEffect, useState} from 'react';
import API from '../api';
import { Link } from 'react-router-dom';

export default function Home(){
  const [restaurants, setRestaurants] = useState([]);

  useEffect(()=> {
    API.get('/restaurants').then(r => setRestaurants(r.data)).catch(console.error);
  }, []);

  return (
    <div>
      <h1>Restaurants</h1>
      <div style={{ display:'grid', gridTemplateColumns:'repeat(auto-fit,minmax(260px,1fr))', gap:20 }}>
        {restaurants.map(r => (
          <div key={r._id} style={{ border:'1px solid #ddd', padding:12, borderRadius:8 }}>
            <img src={r.image || '/placeholder.png'} alt={r.name} style={{ width:'100%', height:140, objectFit:'cover' }} />
            <h3>{r.name}</h3>
            <p>{r.description}</p>
            <Link to={`/restaurant/${r._id}`}>View menu</Link>
          </div>
        ))}
      </div>
    </div>
  )
}
