import React, { useContext } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { loadStripe } from "@stripe/stripe-js";
import { CartContext } from "../context/CartContext"; // adjust path if needed
import {
  Elements,
  CardElement,
  useStripe,
  useElements,
} from "@stripe/react-stripe-js";
import API from "../api";

const stripePromise = loadStripe(
  "pk_test_51SL4gHFrLsiRJStWdnqFsEdHiiDQYDvTOGEe53lwevsgEMz2PxhP4QjKcqgbf8h2N8EKmkIXIDLfELusYLepJqNE00oIRsCveh"
);

function CheckoutForm({ address, cart }) {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();

  // Get clearCart from context
  const { clearCart } = useContext(CartContext);

  const total = cart.reduce((sum, item) => sum + item.price * item.qty, 0);

  const [billingDetails, setBillingDetails] = React.useState({
    name: "",
    email: "",
    line1: "",
    city: "",
    country: "",
  });

  const handleChange = (e) => {
    setBillingDetails({ ...billingDetails, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!stripe || !elements) return;

    try {
      // Step 1: create payment intent
      const { data } = await API.post(
        "/api/payment/create-payment-intent",
        { amount: total * 100 }, // Stripe expects amount in cents
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );

      // Step 2: confirm payment with Stripe
      const result = await stripe.confirmCardPayment(data.clientSecret, {
        payment_method: {
          card: elements.getElement(CardElement),
          billing_details: {
            name: billingDetails.name,
            email: billingDetails.email,
            address: {
              line1: billingDetails.line1,
              city: billingDetails.city,
              country: billingDetails.country,
            },
          },
        },
      });

      if (result.error) {
        alert(result.error.message);
      } else if (
        result.paymentIntent &&
        result.paymentIntent.status === "succeeded"
      ) {
        // Step 3: Save order to DB
        const savedOrder = await API.post(
          "/api/orders",
          {
            items: cart,
            totalAmount: total,
            address: {
              street: address.street,
              postcode: address.postcode,
              county: address.county,
              country: address.country,
            },
            restaurantId: cart[0]?.restaurantId, // ðŸ‘ˆ take restaurantId from the first item
          },
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );

        // clear cart both in context and localStorage
        if (typeof clearCart === "function") clearCart();
        localStorage.removeItem("cart");

        // Step 4: Redirect to confirmation page with order details
        navigate("/order-confirmation", {
          replace: true,
          state: { orderId: savedOrder.data._id, total },
        });
      }
    } catch (err) {
      console.error("Payment error:", err);
      alert("Something went wrong with payment.");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="payment-form">
      <input
        type="text"
        name="name"
        placeholder="Cardholder Name"
        value={billingDetails.name}
        onChange={handleChange}
        required
      />
      <input
        type="email"
        name="email"
        placeholder="Email"
        value={billingDetails.email}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="line1"
        placeholder="Street Address"
        value={billingDetails.line1}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="city"
        placeholder="City"
        value={billingDetails.city}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="country"
        placeholder="Country"
        value={billingDetails.country}
        onChange={handleChange}
        required
      />

      <CardElement />

      <button type="submit" disabled={!stripe} className="btn-primary">
        Pay Â£{total.toFixed(2)}
      </button>
    </form>
  );
}

export default function Payment() {
  const location = useLocation();
  const { address, cart } = location.state || { address: {}, cart: [] };

  return (
    <Elements stripe={stripePromise}>
      <div className="payment-container">
        <h2>Payment</h2>
        <CheckoutForm address={address} cart={cart} />
      </div>
    </Elements>
  );
}
