// client/src/components/Recommendations.js
import React, { useEffect, useState } from "react";
import API from "../api";

export default function Recommendations({ restaurantId }) {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token || !restaurantId) return;

    API.get(`/api/recommendations/${restaurantId}`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => setItems(res.data || []))
      .catch((err) =>
        console.error("‚ùå Recommendation fetch error:", err.message)
      )
      .finally(() => setLoading(false));
  }, [restaurantId]);

  if (loading) return <p>Loading recommendations...</p>;
  if (!items.length) return null;

  return (
    <div style={{ marginTop: "30px" }}>
      <h3>üçΩÔ∏è Recommended for you</h3>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(220px, 1fr))",
          gap: "15px",
          marginTop: "10px",
        }}
      >
        {items.map((item, i) => (
          <div
            key={i}
            style={{
              border: "1px solid #eee",
              borderRadius: "10px",
              padding: "10px",
              background: "#fff",
            }}
          >
            <h4 style={{ marginBottom: "5px" }}>{item.name}</h4>
            <p style={{ fontSize: "0.9em", color: "#555" }}>
              ¬£{item.price?.toFixed(2)}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}
