// client/src/components/Recommendations.js
import React, { useState, useEffect } from "react";
import API from "../api";
import "./Recommendations.css";

export default function Recommendations({ restaurantId }) {
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRecommendations = async () => {
      if (!restaurantId) {
        console.log("No restaurantId provided");
        setLoading(false);
        return;
      }

      try {
        console.log("Fetching recommendations for restaurant:", restaurantId);
        const { data } = await API.get(`/api/recommendations/${restaurantId}`);
        console.log("Received recommendations:", data);
        setRecommendations(data);
      } catch (err) {
        console.error(
          "Failed to fetch recommendations:",
          err.response?.data || err.message
        );
        setError("Could not load recommendations");
      } finally {
        setLoading(false);
      }
    };

    fetchRecommendations();
  }, [restaurantId]);

  if (loading) {
    console.log("Recommendations loading...");
    return <div>Loading recommendations...</div>;
  }

  if (error) {
    console.log("Recommendations error:", error);
    return null;
  }

  if (!recommendations?.length) {
    console.log("No recommendations available");
    return null;
  }

  return (
    <div className="recommendations">
      <h3>Recommended for You</h3>
      <div className="recommendations-grid">
        {recommendations.map((item) => (
          <div key={item._id} className="recommendation-card">
            <div className="recommendation-details">
              <h4>{item.name}</h4>
              <p>{item.description}</p>
              <p className="price">Â£{Number(item.price).toFixed(2)}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
