import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import API from "../api";

export default function Restaurant() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [rest, setRest] = useState(null);

  useEffect(() => {
    API.get(`/restaurants/${id}`)
      .then((r) => setRest(r.data))
      .catch(console.error);
  }, [id]);

  function addToCart(item) {
    const cart = JSON.parse(localStorage.getItem("cart") || "[]");
    const existing = cart.find((i) => i.menuItemId === item._id);
    if (existing) {
      existing.qty += 1;
    } else {
      cart.push({
        menuItemId: item._id,
        name: item.name,
        price: item.price,
        qty: 1,
      });
    }
    localStorage.setItem("cart", JSON.stringify(cart));
    window.dispatchEvent(new Event("storage")); // update navbar badge
  }

  if (!rest) return <div>Loading...</div>;

  return (
    <div className="restaurant-page">
      {/* Banner image */}
      <div className="restaurant-banner">
        <img
          src={`${process.env.REACT_APP_API_URL}${rest.image}`}
          alt={rest.name}
        />
      </div>

      {/* Restaurant Info */}
      <h1>{rest.name}</h1>
      <p>{rest.description}</p>

      {/* Menu */}
      <div className="menu-grid">
        {rest.menu.map((m) => (
          <div key={m._id} className="menu-card">
            <h4>{m.name}</h4>
            <p>{m.description}</p>
            <p>
              <strong>${m.price.toFixed(2)}</strong>
            </p>
            <button className="btn-add" onClick={() => addToCart(m)}>
              ‚ûï Add to Cart
            </button>
          </div>
        ))}
      </div>

      {/* Footer with Choose More Items */}
      <footer className="restaurant-footer">
        <button className="btn-secondary" onClick={() => navigate("/")}>
          üç¥ Choose More Items
        </button>
      </footer>
    </div>
  );
}
