import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { CartContext } from "../context/CartContext";

export default function Checkout() {
  const navigate = useNavigate();
  const { cart } = useContext(CartContext); // get cart here

  const [address, setAddress] = useState({
    street: "",
    postcode: "",
    county: "",
    country: "",
  });

  const handleChange = (e) => {
    setAddress({ ...address, [e.target.name]: e.target.value });
  };

  const handleSubmit = () => {
    if (
      !address.street ||
      !address.postcode ||
      !address.county ||
      !address.country
    ) {
      alert("Please fill out all address fields.");
      return;
    }

    // ✅ Send to OrderReceipt with address + cart
    navigate("/order-receipt", { state: { address, cart } });
  };

  return (
    <div className="checkout-container">
      <h2>Checkout</h2>

      <div className="address-form">
        <label>
          Street Address
          <input
            type="text"
            name="street"
            placeholder="123 Main St"
            value={address.street}
            onChange={handleChange}
          />
        </label>

        <label>
          Postcode
          <input
            type="text"
            name="postcode"
            placeholder="E.g. W1A 1AA"
            value={address.postcode}
            onChange={handleChange}
          />
        </label>

        <label>
          County
          <input
            type="text"
            name="county"
            placeholder="E.g. Greater London"
            value={address.county}
            onChange={handleChange}
          />
        </label>

        <label>
          Country
          <input
            type="text"
            name="country"
            placeholder="E.g. United Kingdom"
            value={address.country}
            onChange={handleChange}
          />
        </label>
      </div>

      <div className="checkout-actions">
        <button className="btn-secondary" onClick={() => navigate("/cart")}>
          ← Back to Cart
        </button>
        <button className="btn-primary" onClick={handleSubmit}>
          Proceed to Payment →
        </button>
      </div>
    </div>
  );
}
