import React, { createContext, useState, useEffect } from "react";

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);

  // Load cart from localStorage on startup
  useEffect(() => {
    const saved = JSON.parse(localStorage.getItem("cart") || "[]");
    setCart(saved);
  }, []);

  // Save cart whenever it changes
  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  const addToCart = (item, restaurantId) => {
    const itemWithRest = { ...item, restaurantId }; // attach restaurantId
    setCart((prev) => {
      const exists = prev.find((i) => i._id === itemWithRest._id);
      if (exists) {
        return prev.map((i) =>
          i._id === itemWithRest._id ? { ...i, qty: i.qty + (itemWithRest.qty || 1) } : i
        );
      }
      return [...prev, { ...itemWithRest, qty: itemWithRest.qty || 1 }];
    });
  };

  const removeFromCart = (id) => {
    setCart((prev) => prev.filter((i) => i.menuItemId !== id));
  };

  const updateQty = (id, qty) => {
    setCart((prev) =>
      prev.map((i) => (i.menuItemId === id ? { ...i, qty } : i))
    );
  };
  const clearCart = () => {
    setCart([]);
  };
  return (
    <CartContext.Provider
      value={{ cart, addToCart, removeFromCart, updateQty, clearCart }}
    >
      {children}
    </CartContext.Provider>
  );
};
