import React, { createContext, useState, useEffect } from "react";

export const CartContext = createContext();

export function CartProvider({ children }) {
  const [cart, setCart] = useState([]);

  // Load cart from localStorage on mount
  useEffect(() => {
    const stored = localStorage.getItem("cart");
    if (stored) setCart(JSON.parse(stored));
  }, []);

  // Always sync localStorage with state
  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  function addToCart(item) {
    setCart((prev) => {
      const existing = prev.find((i) => i.menuItemId === item._id);
      if (existing) {
        return prev.map((i) =>
          i.menuItemId === item._id ? { ...i, qty: i.qty + 1 } : i
        );
      } else {
        return [
          ...prev,
          { menuItemId: item._id, name: item.name, price: item.price, qty: 1 },
        ];
      }
    });
  }

  function removeFromCart(id) {
    setCart((prev) => prev.filter((item) => item.menuItemId !== id));
  }

  function updateQty(id, delta) {
    setCart((prev) =>
      prev
        .map((item) =>
          item.menuItemId === id ? { ...item, qty: item.qty + delta } : item
        )
        .filter((item) => item.qty > 0)
    );
  }

  return (
    <CartContext.Provider value={{ cart, setCart, addToCart, removeFromCart, updateQty }}>
      {children}
    </CartContext.Provider>
  );
}
