import React, { useMemo } from "react";
import { useLocation, Link } from "react-router-dom";

export default function OrderConfirmation() {
  const location = useLocation();
  const { orders, total } = location.state || {};

  // Generate a random delivery time between 20â€“45 minutes
  const eta = useMemo(() => {
    const minutes = Math.floor(Math.random() * 26) + 20;
    const deliveryTime = new Date();
    deliveryTime.setMinutes(deliveryTime.getMinutes() + minutes);
    return { minutes, deliveryTime };
  }, []);

  return (
    <div className="order-confirmation">
      <h2>ðŸŽ‰ Thank you for your order!</h2>

      {orders && orders.length > 0 ? (
        <div className="order-details">
          <p>Your order has been placed successfully.</p>
          {orders.map((order, index) => (
            <div key={order._id} style={{ marginBottom: "1rem" }}>
              <p>
                <strong>Order {orders.length > 1 ? index + 1 : ''} ID:</strong> #{order._id.slice(-6)}
              </p>
              <p>
                <strong>Restaurant:</strong> {order.restaurant?.name || "Unknown"}
              </p>
              <p>
                <strong>Items:</strong>{" "}
                {order.items.map(item => `${item.name} (Ã—${item.qty})`).join(", ")}
              </p>
              <p>
                <strong>Amount:</strong> Â£{order.totalAmount.toFixed(2)}
              </p>
            </div>
          ))}
          <p>
            <strong>Total Paid:</strong> Â£{total.toFixed(2)}
          </p>
          <p>
            <strong>Estimated Delivery:</strong> {eta.minutes} minutes (around{" "}
            {eta.deliveryTime.toLocaleTimeString([], {
              hour: "2-digit",
              minute: "2-digit",
            })}
            )
          </p>
        </div>
      ) : (
        <p>We couldn't fetch order details, but your payment was successful.</p>
      )}

      <div className="order-actions" style={{ marginTop: "2rem" }}>
        <Link 
          to="/profile" 
          className="btn-primary"
          style={{ 
            marginRight: "1rem",
            padding: "0.5rem 1rem",
            background: "#007bff",
            color: "white",
            textDecoration: "none",
            borderRadius: "4px"
          }}
        >
          View My Orders
        </Link>
        <Link 
          to="/" 
          className="btn-secondary"
          style={{ 
            padding: "0.5rem 1rem",
            background: "#6c757d",
            color: "white",
            textDecoration: "none",
            borderRadius: "4px"
          }}
        >
          Continue Shopping
        </Link>
      </div>
    </div>
  );
}
