import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";

export default function Payment() {
  const navigate = useNavigate();
  const location = useLocation();
  const { address, cart } = location.state || { address: {}, cart: [] };

  const [payment, setPayment] = useState({
    cardNumber: "",
    expiry: "",
    cvv: "",
    name: "",
  });

  const total = cart.reduce((sum, item) => sum + item.price * item.qty, 0);

  const handleChange = (e) => {
    setPayment({ ...payment, [e.target.name]: e.target.value });
  };

  const handleSubmit = () => {
    if (
      !payment.cardNumber ||
      !payment.expiry ||
      !payment.cvv ||
      !payment.name
    ) {
      alert("Please fill in all payment details.");
      return;
    }

    // ‚úÖ Dummy payment for now
    alert("Payment successful! üéâ");

    // Clear cart
    localStorage.removeItem("cart");

    // Redirect to thank you page or home
    navigate("/", { replace: true });
  };

  return (
    <div className="payment-container">
      <h2>Payment</h2>

      {/* Address + Cart Summary */}
      <div className="payment-summary">
        <h3>Deliver to:</h3>
        <p>
          {address.street}, {address.postcode}, {address.county},{" "}
          {address.country}
        </p>

        <h3>Your Items:</h3>
        {cart.map((item, index) => (
          <div key={index} className="summary-item">
            {item.name} √ó {item.qty} ‚Äî ¬£{(item.price * item.qty).toFixed(2)}
          </div>
        ))}

        <h3>Total: ¬£{total.toFixed(2)}</h3>
      </div>

      {/* Payment Form */}
      <div className="payment-form">
        <label>
          Cardholder Name
          <input
            type="text"
            name="name"
            placeholder="John Doe"
            value={payment.name}
            onChange={handleChange}
          />
        </label>

        <label>
          Card Number
          <input
            type="text"
            name="cardNumber"
            placeholder="4242 4242 4242 4242"
            value={payment.cardNumber}
            onChange={handleChange}
            inputMode="numeric"
            pattern="[0-9\s]{13,19}"
            maxLength="19"
          />
        </label>

        <div className="payment-row">
          <label>
            Expiry Date
            <input
              type="text"
              name="expiry"
              placeholder="MM/YY"
              value={payment.expiry}
              onChange={handleChange}
            />
          </label>

          <label>
            CVV
            <input
              type="password"
              name="cvv"
              placeholder="123"
              value={payment.cvv}
              onChange={handleChange}
              maxLength="4"
            />
          </label>
        </div>
      </div>

      {/* Actions */}
      <div className="payment-actions">
        <button
          className="btn-secondary"
          onClick={() => navigate("/order-receipt")}
        >
          ‚Üê Back
        </button>
        <button className="btn-primary" onClick={handleSubmit}>
          Pay Now ‚Üí
        </button>
      </div>
    </div>
  );
}
