import React, { useEffect, useState } from "react";
import API from "../api";

export default function Profile() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem("token");
    console.log("ðŸ“¥ token for profile:", token);

    API.get("/api/orders/my-orders", {
      headers: { Authorization: token ? `Bearer ${token}` : undefined },
    })
      .then((res) => {
        console.log("ðŸ“¦ Orders response:", res.data);
        setOrders(res.data);
      })
      .catch((err) => {
        console.error("âŒ Orders fetch error:", err);
      })
      .finally(() => setLoading(false));
  }, []);

  if (loading) {
    return <p>Loading your orders...</p>;
  }

  return (
    <div className="profile-page">
      <h2>My Orders</h2>

      {orders.length === 0 ? (
        <p>You havenâ€™t placed any orders yet.</p>
      ) : (
        <div className="orders-list">
          {orders.map((order) => (
            <div key={order._id} className="order-card">
              <h4>Order #{order._id.slice(-6)}</h4>

              <p>
                <strong>Status:</strong> {order.status}
              </p>
              <p>
                <strong>Total:</strong> Â£{order.totalAmount.toFixed(2)}
              </p>
              <p>
                <strong>Date:</strong>{" "}
                {new Date(order.createdAt).toLocaleString()}
              </p>

              <div className="order-items">
                <strong>Items:</strong>
                {order.items.map((item, i) => (
                  <div key={i} className="order-item">
                    {item.name} Ã— {item.qty} â€” Â£
                    {(item.price * item.qty).toFixed(2)}
                  </div>
                ))}
              </div>

              <div className="order-address">
                <strong>Delivery Address:</strong>
                <p>{order.address?.street}</p>
                <p>
                  {order.address?.postcode}, {order.address?.county}
                </p>
                <p>{order.address?.country}</p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
