import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

export default function OrderReceipt() {
  const location = useLocation();
  const navigate = useNavigate();

  const { address, cart } = location.state || { address: {}, cart: [] };

  if (!cart.length) {
    return (
      <div className="order-receipt">
        <h2>No items in your order</h2>
        <button onClick={() => navigate("/")}>Go Back</button>
      </div>
    );
  }

  return (
    <div className="order-receipt">
      <h1>üéâ Order Confirmation</h1>
      <p>Thank you for your order! Here‚Äôs the summary:</p>

      <h2>Delivery Address</h2>
      <ul>
        <li>
          <strong>Street:</strong> {address.street}
        </li>
        <li>
          <strong>Postcode:</strong> {address.postcode}
        </li>
        <li>
          <strong>County:</strong> {address.county}
        </li>
        <li>
          <strong>Country:</strong> {address.country}
        </li>
      </ul>

      <h2>Your Items</h2>
      <ul>
        {cart.map((item, idx) => (
          <li key={idx}>
            {item.name} √ó {item.qty} ‚Äî ${item.price * item.qty}
          </li>
        ))}
      </ul>

      <h3>
        Total: $
        {cart.reduce((sum, item) => sum + item.price * item.qty, 0).toFixed(2)}
      </h3>

      <button onClick={() => navigate("/")}>üè† Back to Home</button>
    </div>
  );
}
