import React, { useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { CartContext } from "../context/CartContext";

export default function Navbar() {
  const { cart } = useContext(CartContext);
  const user = JSON.parse(localStorage.getItem("user") || "null");
  const token = localStorage.getItem("token");
  const navigate = useNavigate();

  // live count from context
  const cartCount = cart.reduce((s, i) => s + i.qty, 0);

  // check admin from token payload (JWT decode)
  let isAdmin = false;
  if (token) {
    try {
      const payload = JSON.parse(atob(token.split(".")[1]));
      isAdmin = payload.isAdmin;
    } catch (e) {
      console.error("Invalid token decode:", e);
    }
  }

  const handleLogout = () => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    navigate("/login");
    window.location.reload();
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <Link to="/" className="logo-link">
          <img
            src={`${process.env.REACT_APP_API_URL}/static/logo/ohmyfood.png`}
            alt="OhMyFood"
            className="logo"
          />
        </Link>
      </div>

      <div className="navbar-right">
        <Link to="/cart" className="cart-icon">
          ðŸ›’
          {cartCount > 0 && <span className="cart-badge">{cartCount}</span>}
        </Link>

        {user ? (
          <>
            <span className="nav-user">ðŸ‘¤ Hi, {user.name}</span>

            {/* My Orders/Profile link for all users */}
            <Link to="/profile" className="nav-link">
              My Orders
            </Link>

            {/* Admin Dashboard link only for admins */}
            {isAdmin && (
              <Link to="/admin/orders" className="nav-link">
                Admin Dashboard
              </Link>
            )}

            <button onClick={handleLogout} className="logout-btn">
              Logout
            </button>
          </>
        ) : (
          <Link to="/login" className="nav-link">
            Login
          </Link>
        )}
      </div>
    </nav>
  );
}
