import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import API from "../api";

export default function Restaurant() {
  const { id } = useParams();
  const [rest, setRest] = useState(null);

  useEffect(() => {
    API.get(`api/restaurants/${id}`)
      .then((r) => setRest(r.data))
      .catch(console.error);
  }, [id]);

  function addToCart(item) {
    const cart = JSON.parse(localStorage.getItem("cart") || "[]");
    const existing = cart.find((i) => i.menuItemId === item._id);
    if (existing) {
      existing.qty += 1;
    } else {
      cart.push({
        menuItemId: item._id,
        name: item.name,
        price: item.price,
        qty: 1,
      });
    }
    localStorage.setItem("cart", JSON.stringify(cart));
    alert("Added to cart");
  }

  if (!rest) return <div>Loading...</div>;

  return (
    <div>
      <h1>{rest.name}</h1>
      <p>{rest.description}</p>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit,minmax(220px,1fr))",
          gap: 16,
        }}
      >
        {rest.menu.map((m) => (
          <div
            key={m._id || m.name}
            style={{ border: "1px solid #eee", padding: 12 }}
          >
            <h4>
              {m.name} â€” ${m.price.toFixed(2)}
            </h4>
            <p>{m.description}</p>
            <button onClick={() => addToCart(m)}>Add</button>
          </div>
        ))}
      </div>
    </div>
  );
}
