import React, { useEffect, useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { useParams } from "react-router-dom";
import API from "../api";
import { CartContext } from "../context/CartContext";

export default function Restaurant() {
  const { id } = useParams();
  const [rest, setRest] = useState(null);
  const [toast, setToast] = useState(null);
  const { addToCart } = useContext(CartContext);

  useEffect(() => {
    API.get(`/api/restaurants/${id}`)
      .then((r) => setRest(r.data))
      .catch(console.error);
  }, [id]);

  function handleAdd(item) {
    addToCart(item);
    setToast(`${item.name} added to cart ‚úÖ`);
    setTimeout(() => setToast(null), 2000);
  }

  if (!rest) return <div>Loading...</div>;

  return (
    <div className="restaurant-page">
      {toast && <div className="toast">{toast}</div>}

      <div className="restaurant-header">
        <img
          src={`${process.env.REACT_APP_API_URL}${rest.image}`}
          alt={rest.name}
          className="restaurant-banner"
        />
        <div className="restaurant-info">
          <h1>{rest.name}</h1>
          <p>{rest.description}</p>
        </div>
      </div>

      <div className="menu-grid">
        {rest.menu.map((m) => (
          <div key={m._id || m.name} className="menu-card">
            <h4>{m.name}</h4>
            <p>{m.description}</p>
            <span className="price">${m.price.toFixed(2)}</span>
            <button onClick={() => handleAdd(m)} className="add-btn">
              ‚ûï Add to Cart
            </button>
          </div>
        ))}
      </div>
      {/* Footer with Choose More Items */}
      <footer className="restaurant-footer">
        <button className="btn-secondary" onClick={() => navigate("/")}>
          üç¥ Choose More Items
        </button>
      </footer>
    </div>
  );
}
