import React from "react";
import { useLocation } from "react-router-dom";
import { loadStripe } from "@stripe/stripe-js";
import {
  Elements,
  PaymentElement,
  useStripe,
  useElements,
} from "@stripe/react-stripe-js";
import API from "../api";

const stripePromise = loadStripe("pk_test_51SL4gHFrLsiRJStWdnqFsEdHiiDQYDvTOGEe53lwevsgEMz2PxhP4QjKcqgbf8h2N8EKmkIXIDLfELusYLepJqNE00oIRsCveh"); // Replace with your Stripe publishable key

function CheckoutForm({ address, cart }) {
  const stripe = useStripe();
  const elements = useElements();

  const total = cart.reduce((sum, item) => sum + item.price * item.qty, 0);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!stripe || !elements) return;

    // Get a clientSecret from backend
    const { data } = await API.post("/payment/create-payment-intent", {
      amount: total,
    });

    const { error } = await stripe.confirmPayment({
      elements,
      confirmParams: {
        // After payment, Stripe redirects here
        return_url: "http://localhost:3000/order-confirmation",
      },
    });

    if (error) {
      alert(error.message);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="payment-form">
      <PaymentElement />
      <button type="submit" disabled={!stripe} className="btn-primary">
        Pay Â£{total.toFixed(2)}
      </button>
    </form>
  );
}

export default function Payment() {
  const location = useLocation();
  const { address, cart } = location.state || { address: {}, cart: [] };

  return (
    <Elements stripe={stripePromise}>
      <div className="payment-container">
        <h2>Pay with card</h2>
        <CheckoutForm address={address} cart={cart} />
      </div>
    </Elements>
  );
}
