import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import API from "../api";

export default function Restaurant() {
  const { id } = useParams();
  const [rest, setRest] = useState(null);

  useEffect(() => {
    API.get(`/api/restaurants/${id}`)
      .then((r) => setRest(r.data))
      .catch(console.error);
  }, [id]);

  function addToCart(item) {
    const cart = JSON.parse(localStorage.getItem("cart") || "[]");
    const existing = cart.find((i) => i.menuItemId === item._id);
    if (existing) {
      existing.qty += 1;
    } else {
      cart.push({
        menuItemId: item._id,
        name: item.name,
        price: item.price,
        qty: 1,
      });
    }
    localStorage.setItem("cart", JSON.stringify(cart));
    alert(`${item.name} added to cart`);
  }

  if (!rest) return <div className="loading">Loading...</div>;

  return (
    <div className="restaurant-page">
      {/* Header */}
      <div className="restaurant-header">
        <img
          src={`${process.env.REACT_APP_API_URL}${rest.image}`}
          alt={rest.name}
          className="restaurant-banner"
        />
        <div className="restaurant-info">
          <h1>{rest.name}</h1>
          <p>{rest.description}</p>
        </div>
      </div>

      {/* Menu */}
      <div className="menu-grid">
        {rest.menu.map((m) => (
          <div key={m._id || m.name} className="menu-card">
            <h4>{m.name}</h4>
            <p>{m.description}</p>
            <span className="price">${m.price.toFixed(2)}</span>
            <button onClick={() => addToCart(m)} className="add-btn">
              âž• Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
