import React from 'react';
import { Link } from 'react-router-dom';

export default function Navbar(){
  const cartCount = JSON.parse(localStorage.getItem('cart') || '[]').reduce((s, i) => s + i.qty, 0);
  const user = JSON.parse(localStorage.getItem('user') || 'null');

  return (
    <nav style={{ display:'flex', gap:20, padding:10, borderBottom:'1px solid #eee'}}>
      <Link to="/">OhMyFood</Link>
      <Link to="/cart">Cart ({cartCount})</Link>
      {user ? <span>Hi, {user.name}</span> : <Link to="/login">Login</Link>}
    </nav>
  )
}
