import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

export default function OrderSummary() {
  const location = useLocation();
  const navigate = useNavigate();

  // Get cart + address
  const cart = JSON.parse(localStorage.getItem("cart") || "[]");
  const address = JSON.parse(localStorage.getItem("address") || "{}");

  const total = cart.reduce((sum, i) => sum + i.price * i.qty, 0);

  const handleProceed = () => {
    navigate("/payment", { state: { cart, address, total } });
  };

  return (
    <div className="order-summary">
      <h1>Order Summary</h1>

      {/* Address Section */}
      <div className="summary-section">
        <h2>Delivery Address</h2>
        <p>{address.street}</p>
        <p>
          {address.postcode}, {address.county}
        </p>
        <p>{address.country}</p>
        <button onClick={() => navigate("/checkout")} className="btn-secondary">
          ✏️ Edit Address
        </button>
      </div>

      {/* Cart Section */}
      <div className="summary-section">
        <h2>Your Items</h2>
        {cart.length === 0 ? (
          <p>No items in cart.</p>
        ) : (
          cart.map((item) => (
            <div key={item.menuItemId} className="cart-item">
              <span>{item.name}</span>
              <span>
                {item.qty} × ${item.price.toFixed(2)}
              </span>
            </div>
          ))
        )}
        <h3>Total: ${total.toFixed(2)}</h3>
      </div>

      <div className="summary-actions">
        <button onClick={() => navigate("/cart")} className="btn-secondary">
          ← Back to Cart
        </button>
        <button onClick={handleProceed} className="btn-primary">
          Proceed to Payment →
        </button>
      </div>
    </div>
  );
}
