// client/src/pages/Payment.js
import React, { useContext, useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { loadStripe } from "@stripe/stripe-js";
import { CartContext } from "../context/CartContext";
import {
  Elements,
  CardElement,
  useStripe,
  useElements,
} from "@stripe/react-stripe-js";
import API from "../api";

const stripePromise = loadStripe(
  "pk_test_51SL4gHFrLsiRJStWdnqFsEdHiiDQYDvTOGEe53lwevsgEMz2PxhP4QjKcqgbf8h2N8EKmkIXIDLfELusYLepJqNE00oIRsCveh"
);

function CheckoutForm({ address, cart }) {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();
  const { clearCart } = useContext(CartContext);
  const [loading, setLoading] = useState(false);

  const total = cart.reduce((sum, item) => sum + (item.price || 0) * (item.qty || 0), 0);

  const [billingDetails, setBillingDetails] = useState({
    name: "",
    email: "",
    line1: "",
    city: "",
    country: "",
  });

  const [savedAddress, setSavedAddress] = useState(address || {});

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const { data } = await API.get("/api/users/profile", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        if (data?.address) setSavedAddress(data.address);
        if (data?.billingDetails) setBillingDetails(data.billingDetails);
      } catch (err) {
        console.warn("No saved profile / fetch failed:", err?.response?.data || err.message);
      }
    };
    fetchProfile();
  }, []);

  const handleChange = (e) => {
    setBillingDetails({ ...billingDetails, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Basic pre-checks
    const token = localStorage.getItem("token");
    if (!token) {
      alert("You must be logged in to make a payment.");
      return;
    }
    if (!stripe || !elements) {
      alert("Stripe is not ready. Try again in a moment.");
      return;
    }

    setLoading(true);
    try {
      // Create payment intent on server
      const amount = Math.round(total * 100);
      console.log("Creating payment intent for amount (cents):", amount);

      const createResp = await API.post(
        "/api/payment/create-payment-intent",
        { amount },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      console.log("create-payment-intent response:", createResp.data);
      const clientSecret = createResp.data?.clientSecret;
      if (!clientSecret) {
        throw new Error("No clientSecret returned from server.");
      }

      // Confirm card payment
      const card = elements.getElement(CardElement);
      if (!card) throw new Error("Card element not mounted.");

      const confirmResult = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card,
          billing_details: {
            name: billingDetails.name,
            email: billingDetails.email,
            address: {
              line1: billingDetails.line1,
              city: billingDetails.city,
              country: billingDetails.country,
            },
          },
        },
      });

      console.log("stripe.confirmCardPayment result:", confirmResult);
      if (confirmResult.error) {
        // Stripe-level failure (card declined, invalid details, etc.)
        throw confirmResult.error;
      }

      if (confirmResult.paymentIntent?.status !== "succeeded") {
        throw new Error("Payment did not succeed. Status: " + (confirmResult.paymentIntent?.status || "unknown"));
      }

      // Save order to server
      const savedOrderResp = await API.post(
        "/api/orders",
        {
          items: cart,
          totalAmount: total,
          restaurantId: cart.length > 0 ? cart[0].restaurantId : null,
          address: {
            street: savedAddress.street || address.street,
            postcode: savedAddress.postcode || address.postcode,
            county: savedAddress.county || address.county,
            country: savedAddress.country || address.country,
          },
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      console.log("Order saved response:", savedOrderResp.data);

      // Persist billing + address on user profile
      try {
        await API.put(
          "/api/users/profile",
          { address: savedAddress, billingDetails },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        console.log("User profile updated with billing/address.");
      } catch (profileErr) {
        console.warn("Failed to update profile (non-fatal):", profileErr?.response?.data || profileErr.message);
      }

      // Clear cart
      if (typeof clearCart === "function") clearCart();
      localStorage.removeItem("cart");

      // Navigate to confirmation
      navigate("/order-confirmation", {
        state: {
          orderId: savedOrderResp.data._id,
          total: savedOrderResp.data.totalAmount || total,
        },
        replace: true,
      });
    } catch (err) {
      console.error("❌ Payment error:", err);
      // show detailed error if available
      const msg =
        err?.message ||
        err?.type ||
        err?.error ||
        err?.raw?.message ||
        (err?.response && (err.response.data || err.response.statusText)) ||
        "Payment failed. Please check your details and try again.";
      alert(msg);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="payment-form">
      <h3>Billing Information</h3>
      <input type="text" name="name" placeholder="Cardholder Name" value={billingDetails.name} onChange={handleChange} required />
      <input type="email" name="email" placeholder="Email" value={billingDetails.email} onChange={handleChange} required />
      <input type="text" name="line1" placeholder="Street Address" value={billingDetails.line1} onChange={handleChange} required />
      <input type="text" name="city" placeholder="City" value={billingDetails.city} onChange={handleChange} required />
      <input type="text" name="country" placeholder="Country" value={billingDetails.country} onChange={handleChange} required />

      <h3>Payment</h3>
      <div style={{ border: "1px solid #ddd", padding: 10, borderRadius: 6 }}>
        <CardElement />
      </div>

      <button type="submit" disabled={!stripe || loading} className="btn-primary" style={{ marginTop: 12 }}>
        {loading ? "Processing..." : `Pay £${total.toFixed(2)}`}
      </button>
    </form>
  );
}

export default function Payment() {
  const location = useLocation();
  const { address, cart } = location.state || { address: {}, cart: [] };

  return (
    <Elements stripe={stripePromise}>
      <div className="payment-container">
        <h2>Payment</h2>
        <CheckoutForm address={address} cart={cart} />
      </div>
    </Elements>
  );
}
