import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { loadStripe } from "@stripe/stripe-js";
import {
  Elements,
  CardElement,
  useStripe,
  useElements,
} from "@stripe/react-stripe-js";
import API from "../api";

const stripePromise = loadStripe(
  "pk_test_51SL4gHFrLsiRJStWdnqFsEdHiiDQYDvTOGEe53lwevsgEMz2PxhP4QjKcqgbf8h2N8EKmkIXIDLfELusYLepJqNE00oIRsCveh"
); // your publishable key

function CheckoutForm({ address, cart }) {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();

  const total = cart.reduce((sum, item) => sum + item.price * item.qty, 0);

  const [billingDetails, setBillingDetails] = React.useState({
    name: "",
    email: "",
    line1: "",
    city: "",
    country: "",
  });

  const handleChange = (e) => {
    setBillingDetails({ ...billingDetails, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!stripe || !elements) return;

    const { data } = await API.post("/api/payment/create-payment-intent", {
  amount: total * 100, // Stripe expects amount in cents/pence
  currency: "gbp"
});


    const result = await stripe.confirmCardPayment(data.clientSecret, {
      payment_method: {
        card: elements.getElement(CardElement),
        billing_details: {
          name: billingDetails.name,
          email: billingDetails.email,
          address: {
            line1: billingDetails.line1,
            city: billingDetails.city,
            country: billingDetails.country,
          },
        },
      },
    });

    if (result.error) {
      alert(result.error.message);
    } else if (result.paymentIntent.status === "succeeded") {
      await API.post("/orders", {
        cart,
        address,
        total,
      });

      alert("Payment successful ðŸŽ‰ Your order has been placed!");
      localStorage.removeItem("cart");
      navigate("/", { replace: true });
    }
  };

  return (
    <form onSubmit={handleSubmit} className="payment-form">
      <input
        type="text"
        name="name"
        placeholder="Cardholder Name"
        value={billingDetails.name}
        onChange={handleChange}
        required
      />
      <input
        type="email"
        name="email"
        placeholder="Email"
        value={billingDetails.email}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="line1"
        placeholder="Street Address"
        value={billingDetails.line1}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="city"
        placeholder="City"
        value={billingDetails.city}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="country"
        placeholder="Country"
        value={billingDetails.country}
        onChange={handleChange}
        required
      />

      <CardElement />

      <button type="submit" disabled={!stripe} className="btn-primary">
        Pay Â£{total.toFixed(2)}
      </button>
    </form>
  );
}

export default function Payment() {
  const location = useLocation();
  const { address, cart } = location.state || { address: {}, cart: [] };

  return (
    <Elements stripe={stripePromise}>
      <div className="payment-container">
        <h2>Payment</h2>
        <CheckoutForm address={address} cart={cart} />
      </div>
    </Elements>
  );
}
