import React, { useState } from 'react';
import API from '../api';
import { useNavigate } from 'react-router-dom';

export default function Login(){
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [registerMode, setRegisterMode] = useState(false);
  const nav = useNavigate();

  async function submit(e){
    e.preventDefault();
    try {
      if(registerMode){
        await API.post('/auth/register', { name, email, password });
        alert('Registered, please log in');
        setRegisterMode(false);
      } else {
        const res = await API.post('/auth/login', { email, password });
        localStorage.setItem('token', res.data.token);
        localStorage.setItem('user', JSON.stringify(res.data.user));
        nav('/');
      }
    } catch(err){
      console.error(err);
      alert('Auth error');
    }
  }

  return (
    <div style={{ maxWidth:420 }}>
      <h2>{registerMode ? 'Register' : 'Login'}</h2>
      <form onSubmit={submit}>
        {registerMode && <div><label>Name</label><input value={name} onChange={e=>setName(e.target.value)} /></div>}
        <div><label>Email</label><input value={email} onChange={e=>setEmail(e.target.value)} /></div>
        <div><label>Password</label><input type="password" value={password} onChange={e=>setPassword(e.target.value)} /></div>
        <button type="submit">{registerMode ? 'Register' : 'Login'}</button>
      </form>
      <button onClick={()=> setRegisterMode(m => !m)} style={{ marginTop:10 }}>
        {registerMode ? 'Have an account? Login' : 'Need an account? Register'}
      </button>
    </div>
  );
}
