// client/src/pages/Payment.js
import React, { useContext, useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { loadStripe } from "@stripe/stripe-js";
import { CartContext } from "../context/CartContext";
import {
  Elements,
  CardElement,
  useStripe,
  useElements,
} from "@stripe/react-stripe-js";
import API from "../api";

const stripePromise = loadStripe(
  "pk_test_51SL4gHFrLsiRJStWdnqFsEdHiiDQYDvTOGEe53lwevsgEMz2PxhP4QjKcqgbf8h2N8EKmkIXIDLfELusYLepJqNE00oIRsCveh"
);

function CheckoutForm({ address, cart }) {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();
  const { clearCart } = useContext(CartContext);
  const [loading, setLoading] = useState(false);

  const total = cart.reduce((sum, item) => sum + item.price * item.qty, 0);

  const [billingDetails, setBillingDetails] = useState({
    name: "",
    email: "",
    line1: "",
    city: "",
    country: "",
  });

  const [savedAddress, setSavedAddress] = useState(address || {});

  // ✅ Fetch saved user info to prefill the form
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const { data } = await API.get("/api/users/profile", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        if (data?.address) setSavedAddress(data.address);
        if (data?.billingDetails) setBillingDetails(data.billingDetails);
      } catch {
        console.log("No saved user profile found.");
      }
    };
    fetchProfile();
  }, []);

  const handleChange = (e) => {
    setBillingDetails({ ...billingDetails, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!stripe || !elements) return;
    setLoading(true);

    try {
      // 1️⃣ Create payment intent
      const { data } = await API.post(
        "/api/payment/create-payment-intent",
        { amount: Math.round(total * 100) },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );

      const clientSecret = data?.clientSecret;
      if (!clientSecret) throw new Error("Missing clientSecret from server.");

      // 2️⃣ Confirm payment with Stripe
      const card = elements.getElement(CardElement);
      const result = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card,
          billing_details: {
            name: billingDetails.name,
            email: billingDetails.email,
            address: {
              line1: billingDetails.line1,
              city: billingDetails.city,
              country: billingDetails.country,
            },
          },
        },
      });

      if (result.error) {
        console.error("Stripe confirm error:", result.error);
        alert(result.error.message);
        setLoading(false);
        return;
      }

      // 3️⃣ On successful payment
      if (result.paymentIntent?.status === "succeeded") {
        // ✅ Save order
        const savedOrder = await API.post(
          "/api/orders",
          {
            items: cart,
            totalAmount: total,
            restaurantId: cart.length > 0 ? cart[0].restaurantId : null,
            address: {
              street: savedAddress.street || address.street,
              postcode: savedAddress.postcode || address.postcode,
              county: savedAddress.county || address.county,
              country: savedAddress.country || address.country,
            },
          },
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );

        // ✅ Save user's billing + address info for future
        await API.put(
          "/api/users/profile",
          { address: savedAddress, billingDetails },
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );

        // ✅ Clear cart
        if (typeof clearCart === "function") clearCart();
        localStorage.removeItem("cart");

        console.log("✅ Order & profile saved:", savedOrder.data);

        navigate("/order-confirmation", {
          state: {
            orderId: savedOrder.data._id,
            total: savedOrder.data.totalAmount || total,
          },
          replace: true,
        });
      }
    } catch (err) {
      console.error("❌ Payment error:", err);
      alert("Payment failed. Please check your details and try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="payment-form">
      <h3>Billing Information</h3>
      <input
        type="text"
        name="name"
        placeholder="Cardholder Name"
        value={billingDetails.name}
        onChange={handleChange}
        required
      />
      <input
        type="email"
        name="email"
        placeholder="Email"
        value={billingDetails.email}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="line1"
        placeholder="Street Address"
        value={billingDetails.line1}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="city"
        placeholder="City"
        value={billingDetails.city}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="country"
        placeholder="Country"
        value={billingDetails.country}
        onChange={handleChange}
        required
      />

      <h3>Payment</h3>
      <CardElement />

      <button
        type="submit"
        disabled={!stripe || loading}
        className="btn-primary"
      >
        {loading ? "Processing..." : `Pay £${total.toFixed(2)}`}
      </button>
    </form>
  );
}

export default function Payment() {
  const location = useLocation();
  const { address, cart } = location.state || { address: {}, cart: [] };

  return (
    <Elements stripe={stripePromise}>
      <div className="payment-container">
        <h2>Payment</h2>
        <CheckoutForm address={address} cart={cart} />
      </div>
    </Elements>
  );
}
